import { Component } from '@angular/core';
import { UsersService } from '../users.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  email: string;
  password: string;
  userService: UsersService;

  constructor() { }

  login() {
    console.log(this.email);
    console.log(this.password);
    const user = { email: this.email, password: this.password };
    this.userService.login(user).subscribe(data => {
      console.log(data);
    });
  }

}
<div class="login">
    <form class="custom-form" method="post" #loginForm="ngForm">
        <h1>Iniciar Sesión</h1>
        <input type="email" [(ngModel)]="email" name="email" placeholder="Email" required="required" />
        <input type="password" [(ngModel)]="password" name="password" placeholder="Contraseña" required="required" />
        <button type="submit" (click)="login()">Iniciar sesión</button>
    </form>
</div>
